import UserProfile from '../components/UserProfile';
export const TOKEN_EMAIL = "user_email";
export const TOKEN_TYPE = "user_type";
export const isAuthenticated = () => {
  console.log(UserProfile.getEmail());
  return UserProfile.getEmail() != null;


}
export const getTokenEmail = () => localStorage.getItem(TOKEN_EMAIL);
export const logout = () => {
  UserProfile.setTipo(null);
  UserProfile.setEmail(null);
};




export default function getUsers()  {
  return fetch('http://localhost:3000/user')
        .then((response) => { 
            return response.json().then((data) => {

                return data;
            }).catch((err) => {
                console.log(err);
            }) 
        });
}
