import React, {useState, Fragment, Component} from 'react'
import AddCabeleireiroForm from '../crud/AddCabeleireiroForm';
import EditCabeleireiroForm from '../crud/EditCabeleireiroForm';
import CabeleireiroTable from '../crud/CabeleireiroTable';
import NavbarDono from "../components/NavbarDono";
import '../crud/style.css';
import api from "../services/api";
import NavbarCliente from "../components/NavbarCliente";


export default class Gestao extends Component{
	state={
		name:"",
		postalCode:"",
		city:"",
		country:"",
		status:"",
		type:"",
		contact:"",
		description:"",
		image:"",
		address:"",
	};
	getSrc() {

	}
	/*api.post("/user/",{name: "Joao Carvalho",email: "joao@gmail.com",password: "123",type: "dono"} ).then(id => {
    api.post("/saloon/",{name: "Machos",postalCode: "3100-412",city: "Tarouca",country: "Portugal",status: "Aberto",type: "Cabeleireiro",contact: "911881428",description: "O melhor cabeleireiro de Portugal e arredores!",image: "https://fotos.vivadecora.com.br/decoracao-salao-de-beleza-piso-de-porcelanato-claro-e-cadeira-de-couro-preta-revistavd-187516-square_cover_xsmall.jpg",address: "Rua Cima do Povo",owner: id.data} ).then();
	});*/	// Data
	render() {

		let cabeleireirosData = api.get("/saloon/",).then(function
				(res) {
				return res.data;
			}
		);

		console.log(cabeleireirosData);
		const initialFormState = {id: null, nome: '', cidade: '', imagem: ''};

		// Setting state
		const [cabeleireiros, setCabeleireiros] = useState(cabeleireirosData);
		const [currentCabeleireiro, setCurrentCabeleireiro] = useState(initialFormState);
		const [editing, setEditing] = useState(false);

		// CRUD operations
		const addCabeleireiro = cabeleireiro => {
			cabeleireiro.id = cabeleireiros.length + 1
			setCabeleireiros([...cabeleireiros, cabeleireiro])
		}

		const deleteCabeleireiro = id => {
			setEditing(false)

			setCabeleireiros(cabeleireiros.filter(cabeleireiro => cabeleireiro.id !== id))
		}

		const updateCabeleireiro = (id, updatedCabeleireiro) => {
			setEditing(false)

			setCabeleireiros(cabeleireiros.map(cabeleireiro => (cabeleireiro.id === id ? updatedCabeleireiro : cabeleireiro)))
		}

		const editRow = cabeleireiro => {
			setEditing(true)

			setCurrentCabeleireiro({
				id: cabeleireiro.id,
				nome: cabeleireiro.nome,
				cidade: cabeleireiro.cidade,
				imagem: cabeleireiro.imagem
			})
		}

		return (
			<>
				{localStorage.getItem("user_type")=="dono" ? <NavbarDono></NavbarDono>:<NavbarCliente></NavbarCliente> }
			<Fragment>
				<NavbarDono></NavbarDono>
				<div className="container">
					<h1>CRUD dos cabeleireiros no Web Site</h1>
					<div className="flex-row">
						<div className="flex-large">
							{editing ? (
								<Fragment>
									<h2>Editar cabeleireiro</h2>
									<EditCabeleireiroForm
										editing={editing}
										setEditing={setEditing}
										currentCabeleireiro={currentCabeleireiro}
										updateCabeleireiro={updateCabeleireiro}
									/>
								</Fragment>
							) : (
								<Fragment>
									<h2>Adicionar cabeleireiro</h2>
									<AddCabeleireiroForm addCabeleireiro={addCabeleireiro}/>
								</Fragment>
							)}
						</div>
						<div className="flex-large">
							<h2>Ver cabeleireiros</h2>
							<CabeleireiroTable cabeleireiros={cabeleireiros} editRow={editRow}
											   deleteCabeleireiro={deleteCabeleireiro}/>
						</div>
					</div>
				</div>
			</Fragment>
				</>
		)
	}
}
