import React, { useState } from 'react'
import api from "../services/api";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";

const AddCabeleireiroForm = props => {
	const initialFormState = { id: null, name: '', postalCode: '', city: '', country: '', status: '', type: '', contact: '', description: '', address: ''}
	const [ cabeleireiro, setCabeleireiro ] = useState(initialFormState)

	const [services, setServices] = React.useState({
		cortes: false,
		manicure_pedicure: false,
		escovas: false,
		tratamento_capilar: false,
		serviços_especiais: false,
		depilacao: false,
		maquilhagem: false,
		quimicas: false,
	});
	const [hours, setHours] = React.useState({
		cortesHours: [],
		manicure_pedicureHours: [],
		escovasHours: [],
		tratamento_capilarHours: [],
		serviços_especiaisHours: [],
		depilacaoHours: [],
		maquilhagemHours: [],
		quimicasHours: [],
	});

	const handleToggle = ({ target }) =>
		setServices(s => ({ ...s, [target.name]: !s[target.name] }));

	const handleHourChange = event => {
		const { name, value } = event.target
		setHours({ ...hours, [name]: value.split(",")  });
	}

	const handleInputChange = event => {
		const { name, value } = event.target;
		setCabeleireiro({ ...cabeleireiro, [name]: value })
	}

	return (
		<form
			onSubmit={event => {
				event.preventDefault()
				if (!cabeleireiro.name || !cabeleireiro.city) return

				console.log(services)

				api.get("/user/",{params:{email: "joao@hotmail.com"}}).then(function (response) {
					console.log(response.data);
					let data = response.data;
					let res;
					for(const i in data){
						for(const n in data[i]){
							if(data[i].email === localStorage.getItem("user_email")){
								res = data[i];
							}
						}
					}
					console.log(res);
					//console.log(final);
					api.post("/saloon/",{name: cabeleireiro.name,postalCode: cabeleireiro.postalCode, city: cabeleireiro.city ,country: cabeleireiro.country, status: cabeleireiro.status, type: cabeleireiro.type, contact: cabeleireiro.contact, description: cabeleireiro.description
					 					,address: cabeleireiro.address, owner: res} ).then(res =>{
					 						console.log(res)
					 				});
				})
				setCabeleireiro(initialFormState)
			}}
		>
			<label>Nome</label>
			<input type="text" name="name" value={cabeleireiro.name} onChange={handleInputChange} />
			<label>Postal Code</label>
			<input type="text" name="postalCode" value={cabeleireiro.postalCode} onChange={handleInputChange} />
			<label>Cidade</label>
			<input type="text" name="city" value={cabeleireiro.city} onChange={handleInputChange} />
			<label>Country</label>
			<input type="text" name="country" value={cabeleireiro.country} onChange={handleInputChange} />
			<label>Status</label>
			<input type="text" name="status" value={cabeleireiro.status} onChange={handleInputChange} />
			<label>Type</label>
			<input type="text" name="type" value={cabeleireiro.type} onChange={handleInputChange} />
			<label>Contact</label>
			<input type="text" name="contact" value={cabeleireiro.contact} onChange={handleInputChange} />
			<label>Description</label>
			<input type="text" name="description" value={cabeleireiro.description} onChange={handleInputChange} />
			<label>Address</label>
			<input type="text" name="address" value={cabeleireiro.address} onChange={handleInputChange} />
			<label>Pick your services</label>
			<FormGroup>
				<FormControlLabel
					control={<Checkbox checked={services.cortes} onChange={handleToggle} name="cortes" />}
					label="Cortes"
				/>
				<input type="text" name="cortesHours" value={hours.cortesHours} onChange={handleHourChange}  placeholder="Type your available schedules(09:00:00 P ex.)"/>
				<FormControlLabel
					control={<Checkbox checked={services.manicure_pedicure} onChange={handleToggle} name="manicure_pedicure" />}
					label="Manicure Pedicure"
				/>
				<input type="text" name="manicure_pedicureHours" value={hours.manicure_pedicureHours} onChange={handleHourChange} placeholder="Type your available schedules(09:00:00 P ex.)" />
				<FormControlLabel
					control={<Checkbox checked={services.escovas} onChange={handleToggle} name="escovas" />}
					label="Escovas"
				/>
				<input type="text" name="escovasHours" value={hours.escovasHours} onChange={handleHourChange} placeholder="Type your available schedules(09:00:00 P ex.)" />
				<FormControlLabel
					control={<Checkbox checked={services.tratamento_capilar} onChange={handleToggle} name="tratamento_capilar" />}
					label="Tratamento Capilar"
				/>
				<input type="text" name="tratamento_capilarHours" value={hours.tratamento_capilarHours} onChange={handleHourChange} placeholder="Type your available schedules(09:00:00 P ex.)" />
				<FormControlLabel
					control={<Checkbox checked={services.serviços_especiais} onChange={handleToggle} name="serviços_especiais" />}
					label="Serviços Especiais"
				/>
				<input type="text" name="serviços_especiaisHours" value={hours.serviços_especiaisHours} onChange={handleHourChange} placeholder="Type your available schedules(09:00:00 P ex.)" />
				<FormControlLabel
					control={<Checkbox checked={services.depilacao} onChange={handleToggle} name="depilacao" />}
					label="Depilação"
				/>
				<input type="text" name="depilacaoHours" value={hours.depilacaoHours} onChange={handleHourChange} placeholder="Type your available schedules(09:00:00 P ex.)" />
				<FormControlLabel
					control={<Checkbox checked={services.maquilhagem} onChange={handleToggle} name="maquilhagem" />}
					label="Maquilhagem"
				/>
				<input type="text" name="maquilhagemHours" value={hours.maquilhagemHours} onChange={handleHourChange} placeholder="Type your available schedules(09:00:00 P ex.)" />
				<FormControlLabel
					control={<Checkbox checked={services.quimicas} onChange={handleToggle} name="quimicas" />}
					label="Quimicas"
				/>
				<input type="text" name="quimicasHours" value={hours.quimicasHours} onChange={handleHourChange} placeholder="Type your available schedules(09:00:00 P ex.)" />
			</FormGroup>
			<button>Adicionar novo cabeleireiro</button>
		</form>
	)
}

export default AddCabeleireiroForm
